INSERT INTO `foods` (`food_id`, `food_name`, `description`, `thumbnail`, `content`, `category_id`, `preparation_time`, `total_minute`, `user_id`, `created_at`, `updated_at`) VALUES
	(1, 'Phở Gà', 'Phở gà truyền thống với nước dùng ngọt thanh', 'https://i-giadinh.vnecdn.net/2023/08/18/Bc9Thnhphm9-1692350453-3832-1692350464.jpg', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 4 người)</strong></p>\r\n<h4>Gà:</h4>\r\n<ul>\r\n<li>\r\n<p>1 con gà ta (1.2–1.5kg)</p>\r\n</li>\r\n<li>\r\n<p>Gừng nướng: 1 củ (~50g)</p>\r\n</li>\r\n<li>\r\n<p>Hành tím nướng: 2–3 củ</p>\r\n</li>\r\n<li>\r\n<p>Hành tây nướng: 1 củ</p>\r\n</li>\r\n<li>\r\n<p>Muối hạt, tiêu, hạt nêm, đường phèn</p>\r\n</li>\r\n</ul>\r\n<h4>Gia vị tạo hương vị:</h4>\r\n<ul>\r\n<li>\r\n<p>Hoa hồi: 3–4 cánh</p>\r\n</li>\r\n<li>\r\n<p>Thảo quả: 1–2 quả</p>\r\n</li>\r\n<li>\r\n<p>Quế: 1 thanh</p>\r\n</li>\r\n<li>\r\n<p>Đinh hương: vài cái</p>\r\n</li>\r\n<li>\r\n<p>Rễ ngò (nếu có): 1–2 rễ</p>\r\n</li>\r\n</ul>\r\n<h4>Khác:</h4>\r\n<ul>\r\n<li>\r\n<p>Bánh phở tươi hoặc khô</p>\r\n</li>\r\n<li>\r\n<p>Hành lá, rau mùi, rau thơm</p>\r\n</li>\r\n<li>\r\n<p>Giá đỗ, chanh, ớt, tiêu</p>\r\n</li>\r\n<li>\r\n<p>Nước mắm ngon</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<h4><strong>Bước 1: Sơ chế gà và nguyên liệu</strong></h4>\r\n<ol>\r\n<li>\r\n<p>Làm sạch gà với muối và gừng đập dập để khử mùi.</p>\r\n</li>\r\n<li>\r\n<p>Gừng, hành tím và hành tây: nướng đến khi cháy xém, sau đó đập dập (giúp nước dùng thơm hơn).</p>\r\n</li>\r\n<li>\r\n<p>Gia vị như quế, hồi, thảo quả, đinh hương: rang sơ để dậy mùi, sau đó cho vào túi vải buộc kín.</p>\r\n</li>\r\n</ol>\r\n<p><hr></p>\r\n<h4><strong>Bước 2: Nấu nước dùng</strong></h4>\r\n<ol>\r\n<li>\r\n<p>Đun sôi khoảng 2.5–3 lít nước, cho gà vào luộc cùng gừng, hành nướng.</p>\r\n</li>\r\n<li>\r\n<p>Khi nước sôi, hạ lửa nhỏ, hớt bọt thường xuyên để nước trong.</p>\r\n</li>\r\n<li>\r\n<p>Sau 10 phút, cho túi gia vị vào nồi. Luộc gà thêm 20–25 phút tùy theo trọng lượng.</p>\r\n</li>\r\n<li>\r\n<p>Khi gà chín, vớt ra, nhúng ngay vào nước đá lạnh để da giòn, thịt săn.</p>\r\n</li>\r\n<li>\r\n<p>Gà nguội, lọc lấy thịt, thái lát vừa ăn.</p>\r\n</li>\r\n</ol>\r\n<p><hr></p>\r\n<h4><strong>Bước 3: Chuẩn bị bánh phở và rau</strong></h4>\r\n<ol>\r\n<li>\r\n<p>Trụng bánh phở qua nước sôi để mềm, cho ra bát.</p>\r\n</li>\r\n<li>\r\n<p>Rau thơm, hành lá, giá rửa sạch, để ráo.</p>\r\n</li>\r\n<li>\r\n<p>Hành lá cắt khúc, hành tây thái lát mỏng (ngâm nước đá nếu muốn giòn).</p>\r\n</li>\r\n</ol>\r\n<p><hr></p>\r\n<h4><strong>Bước 4: Nêm nước dùng</strong></h4>\r\n<ul>\r\n<li>\r\n<p>Nêm nước dùng với muối, đường phèn, hạt nêm cho vừa ăn.</p>\r\n</li>\r\n<li>\r\n<p>Có thể thêm nước mắm nếu thích hương vị đậm đà.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h4><strong>Bước 5: Trình bày</strong></h4>\r\n<ol>\r\n<li>\r\n<p>Cho bánh phở vào tô.</p>\r\n</li>\r\n<li>\r\n<p>Xếp thịt gà, hành lá, hành tây lên trên.</p>\r\n</li>\r\n<li>\r\n<p>Chan nước dùng nóng ngập bánh phở.</p>\r\n</li>\r\n<li>\r\n<p>Ăn kèm chanh, ớt, tiêu, giá, rau thơm tùy khẩu vị.</p>\r\n</li>\r\n</ol>\r\n<p><hr></p>\r\n<h3><strong>Mẹo nhỏ</strong></h3>\r\n<ul>\r\n<li>\r\n<p><strong>Gà ta</strong> sẽ cho nước ngọt và thịt dai ngon hơn.</p>\r\n</li>\r\n<li>\r\n<p><strong>Không đậy nắp khi ninh nước dùng</strong> để giữ nước trong.</p>\r\n</li>\r\n<li>\r\n<p><strong>Có thể xương gà chặt nhỏ, ninh riêng thêm vài giờ</strong> để tăng độ ngọt cho nước.<br></p></li></ul>', 1, 20, 60, 1, '2025-07-16 18:25:21', '2025-07-16 19:06:53'),
	(2, 'Hủ Tiếu Nam Vang', 'Món hủ tiếu đậm đà của người miền Nam', 'https://www.huongnghiepaau.com/wp-content/uploads/2019/01/hu-tieu-nam-vang-1.jpg', '<p><strong style="font-size: 0.9375rem;">Nguyên liệu chuẩn bị (cho 4–5 người)</strong></p>\r\n<p><em>Phần nước dùng:</em></p>\r\n<ul>\r\n<li>\r\n<p>Xương heo: 1kg</p>\r\n</li>\r\n<li>\r\n<p>Tôm sú: 300g</p>\r\n</li>\r\n<li>\r\n<p>Mực khô: 1 con (nhỏ)</p>\r\n</li>\r\n<li>\r\n<p>Củ cải trắng: 1–2 củ</p>\r\n</li>\r\n<li>\r\n<p>Hành tím nướng: 3–4 củ</p>\r\n</li>\r\n<li>\r\n<p>Tỏi, hành phi</p>\r\n</li>\r\n<li>\r\n<p>Muối, đường phèn, hạt nêm, nước mắm</p>\r\n</li>\r\n</ul>\r\n<p><em>Phần topping:</em></p>\r\n<ul>\r\n<li>\r\n<p>Gan heo: 200g</p>\r\n</li>\r\n<li>\r\n<p>Thịt nạc heo xay: 200g</p>\r\n</li>\r\n<li>\r\n<p>Tôm luộc: từ phần trên</p>\r\n</li>\r\n<li>\r\n<p>Trứng cút: 15–20 quả</p>\r\n</li>\r\n<li>\r\n<p>Tim, cật hoặc lòng heo (tuỳ chọn)</p>\r\n</li>\r\n</ul>\r\n<p><em>Phần khác:</em></p>\r\n<ul>\r\n<li>\r\n<p>Hủ tiếu khô hoặc tươi</p>\r\n</li>\r\n<li>\r\n<p>Hẹ, giá đỗ, rau xà lách, cần tàu</p>\r\n</li>\r\n<li>\r\n<p>Hành phi, tỏi phi</p>\r\n</li>\r\n<li>\r\n<p>Ớt, chanh, tiêu</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<p><strong>Quy trình chế biến</strong></p>\r\n<p><strong>Bước 1: Sơ chế nguyên liệu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Xương heo rửa sạch, chần sơ qua nước sôi để loại bỏ tạp chất.</p>\r\n</li>\r\n<li>\r\n<p>Gan, tim, cật rửa sạch với nước muối và gừng, chần sơ và thái lát.</p>\r\n</li>\r\n<li>\r\n<p>Tôm bóc vỏ, chừa đuôi nếu muốn trang trí đẹp. Luộc chín, để riêng.</p>\r\n</li>\r\n<li>\r\n<p>Trứng cút luộc chín, bóc vỏ.</p>\r\n</li>\r\n<li>\r\n<p>Củ cải trắng gọt vỏ, cắt khoanh lớn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Nấu nước dùng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho xương heo, củ cải trắng, hành tím nướng, mực khô nướng vào nồi, đổ khoảng 3–4 lít nước.</p>\r\n</li>\r\n<li>\r\n<p>Ninh nước dùng ít nhất 1,5–2 giờ ở lửa nhỏ. Hớt bọt thường xuyên để nước trong.</p>\r\n</li>\r\n<li>\r\n<p>Nêm nước dùng với muối, đường phèn, hạt nêm. Không nên nêm nước mắm vào nước lèo để tránh bị đục.</p>\r\n</li>\r\n<li>\r\n<p>Khi gần dùng, có thể lọc lại nước cho trong hơn nếu muốn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Xào thịt</strong></p>\r\n<ul>\r\n<li>\r\n<p>Phi tỏi cho thơm, cho thịt heo xay vào xào chín với chút hạt nêm, nước mắm. Để riêng.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Luộc topping</strong></p>\r\n<ul>\r\n<li>\r\n<p>Gan, tim, cật: luộc riêng từng loại trong nước sôi, tránh luộc quá lâu làm dai.</p>\r\n</li>\r\n<li>\r\n<p>Tôm đã luộc ở bước trên.</p>\r\n</li>\r\n<li>\r\n<p>Trứng cút đã bóc sẵn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 5: Chuẩn bị rau và hủ tiếu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Giá đỗ rửa sạch, trụng sơ nếu muốn.</p>\r\n</li>\r\n<li>\r\n<p>Hẹ cắt khúc, xà lách, cần tàu thái nhỏ.</p>\r\n</li>\r\n<li>\r\n<p>Hủ tiếu tươi trụng nước sôi cho mềm. Nếu dùng hủ tiếu khô thì luộc theo hướng dẫn trên bao bì, xả nước lạnh và trụng lại nước sôi.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 6: Trình bày tô hủ tiếu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho hủ tiếu vào tô.</p>\r\n</li>\r\n<li>\r\n<p>Xếp lần lượt: thịt bằm xào, gan, tôm, trứng cút, lòng heo (nếu có).</p>\r\n</li>\r\n<li>\r\n<p>Chan nước dùng nóng vào tô.</p>\r\n</li>\r\n<li>\r\n<p>Rắc hành phi, tỏi phi, hẹ và thêm rau sống tùy thích.</p>\r\n</li>\r\n<li>\r\n<p>Dọn kèm chanh, ớt, nước mắm pha.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<p><strong>Lưu ý</strong></p>\r\n<ul>\r\n<li>\r\n<p>Có thể dùng tôm khô nướng sơ rồi nấu chung với nước dùng để tăng độ ngọt.</p>\r\n</li>\r\n<li>\r\n<p>Gan và lòng nên luộc riêng từng loại để tránh mùi lẫn nhau.</p>\r\n</li>\r\n<li>\r\n<p>Nếu thích kiểu "hủ tiếu khô", chan một ít nước dùng riêng và trộn hủ tiếu với nước sốt nêm đậm đà.<br></p></li></ul>', 1, 25, 70, 1, '2025-07-16 18:25:21', '2025-07-16 19:08:09'),
	(3, 'Bún Bò Huế', 'Đặc sản miền Trung cay nồng', 'https://daivietourist.vn/wp-content/uploads/2025/04/quan-bun-bo-hue-1.jpg', '<p><strong style="font-size: 0.9375rem;">Nguyên liệu chuẩn bị (cho 6–8 người)</strong></p>\r\n<p><em>Phần nước dùng:</em></p>\r\n<ul>\r\n<li>\r\n<p>Xương bò: 1–1.5kg (xương ống, xương đuôi)</p>\r\n</li>\r\n<li>\r\n<p>Giò heo (chặt khoanh): 1 chiếc (khoảng 700g)</p>\r\n</li>\r\n<li>\r\n<p>Thịt bắp bò: 500g</p>\r\n</li>\r\n<li>\r\n<p>Chả Huế (chả lụa, chả cua): tuỳ chọn</p>\r\n</li>\r\n<li>\r\n<p>Sả: 6–8 cây, đập dập</p>\r\n</li>\r\n<li>\r\n<p>Gừng: 1 củ lớn, nướng thơm</p>\r\n</li>\r\n<li>\r\n<p>Hành tím: 5–6 củ, nướng</p>\r\n</li>\r\n<li>\r\n<p>Mắm ruốc Huế: 2–3 muỗng canh</p>\r\n</li>\r\n<li>\r\n<p>Ớt bột, màu điều, dầu ăn</p>\r\n</li>\r\n</ul>\r\n<p><em>Gia vị:</em></p>\r\n<ul>\r\n<li>\r\n<p>Muối, đường phèn, hạt nêm, nước mắm ngon</p>\r\n</li>\r\n</ul>\r\n<p><em>Phần khác:</em></p>\r\n<ul>\r\n<li>\r\n<p>Bún sợi to (loại dùng cho Bún Bò Huế)</p>\r\n</li>\r\n<li>\r\n<p>Rau ăn kèm: bắp chuối bào, rau thơm, giá, húng quế, rau răm</p>\r\n</li>\r\n<li>\r\n<p>Hành lá, ngò gai, chanh, ớt tươi, hành phi</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<p><strong>Quy trình chế biến</strong></p>\r\n<p><strong>Bước 1: Sơ chế nguyên liệu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Xương bò và giò heo rửa sạch, trụng qua nước sôi để khử mùi và loại bỏ bọt bẩn.</p>\r\n</li>\r\n<li>\r\n<p>Bắp bò buộc chặt (nếu cần) để luộc giữ hình dạng.</p>\r\n</li>\r\n<li>\r\n<p>Sả đập dập, cắt khúc; hành tím và gừng nướng cho dậy mùi thơm.</p>\r\n</li>\r\n<li>\r\n<p>Mắm ruốc Huế pha với nước lạnh, khuấy đều rồi để lắng, gạn lấy phần nước trong.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Nấu nước dùng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho xương bò, giò heo vào nồi lớn, thêm khoảng 4–5 lít nước. Đun sôi và hạ lửa nhỏ, hớt bọt thường xuyên.</p>\r\n</li>\r\n<li>\r\n<p>Cho sả, hành tím nướng, gừng nướng vào nồi.</p>\r\n</li>\r\n<li>\r\n<p>Luộc khoảng 1 tiếng thì cho bắp bò vào nấu thêm 45 phút đến 1 tiếng (tùy độ dày). Khi bắp bò chín thì vớt ra, để nguội rồi thái lát mỏng.</p>\r\n</li>\r\n<li>\r\n<p>Giò heo luộc đến khi mềm vừa phải thì vớt ra để ráo.</p>\r\n</li>\r\n<li>\r\n<p>Thêm phần nước mắm ruốc đã gạn trong vào nồi nước dùng.</p>\r\n</li>\r\n<li>\r\n<p>Nêm nếm với muối, đường phèn, nước mắm, hạt nêm cho đậm đà.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Phi màu điều và ớt bột</strong></p>\r\n<ul>\r\n<li>\r\n<p>Đun nóng dầu ăn, cho hạt điều màu (hoặc bột điều) vào tạo màu đỏ cam.</p>\r\n</li>\r\n<li>\r\n<p>Lọc lấy dầu, thêm ớt bột khô vào phi thơm (không để cháy), rồi cho hỗn hợp này vào nồi nước dùng để tạo màu và vị cay nhẹ đặc trưng.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Chuẩn bị bún và rau</strong></p>\r\n<ul>\r\n<li>\r\n<p>Bún luộc hoặc trụng sơ qua nước sôi, để ráo.</p>\r\n</li>\r\n<li>\r\n<p>Rau sống rửa sạch, để ráo nước.</p>\r\n</li>\r\n<li>\r\n<p>Hành lá, ngò gai thái nhỏ để rắc lên tô bún.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 5: Trình bày</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho bún vào tô.</p>\r\n</li>\r\n<li>\r\n<p>Xếp thịt bắp bò, giò heo, chả lụa hoặc chả cua (nếu có) lên mặt.</p>\r\n</li>\r\n<li>\r\n<p>Chan nước dùng nóng vào ngập mặt bún.</p>\r\n</li>\r\n<li>\r\n<p>Rắc hành lá, ngò gai, hành phi lên trên.</p>\r\n</li>\r\n<li>\r\n<p>Ăn kèm rau sống, ớt tươi, chanh và nước mắm nguyên chất.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<p><strong>Lưu ý quan trọng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Mắm ruốc nên lọc kỹ để không làm đục nước.</p>\r\n</li>\r\n<li>\r\n<p>Màu điều và ớt bột giúp tạo màu hấp dẫn và vị cay đặc trưng, có thể điều chỉnh lượng theo khẩu vị.</p>\r\n</li>\r\n<li>\r\n<p>Nếu thích hương vị đậm chất Huế, có thể thêm ít sả băm và tiêu khi phi màu điều.<br></p></li></ul>', 1, 30, 90, 1, '2025-07-16 18:25:21', '2025-07-16 19:08:41'),
	(4, 'Chè Đậu Xanh', 'Chè đậu xanh thanh mát', 'https://homestory.com.vn/wp-content/uploads/2023/05/che-dau-xanh.jpg', '<p><span style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px; font-weight: 700; display: inline !important;">Nguyên liệu chuẩn bị (cho 4–5 người)</span></p>\r\n<ul>\r\n<li>\r\n<p>Đậu xanh cà vỏ: 200g</p>\r\n</li>\r\n<li>\r\n<p>Đường cát trắng: 150–200g (tùy khẩu vị)</p>\r\n</li>\r\n<li>\r\n<p>Nước lọc: 1–1.2 lít</p>\r\n</li>\r\n<li>\r\n<p>Lá dứa (tùy chọn): 3–4 lá</p>\r\n</li>\r\n<li>\r\n<p>Muối: 1/4 thìa cà phê</p>\r\n</li>\r\n<li>\r\n<p>Bột năng hoặc bột sắn dây: 1–2 muỗng canh (để tạo độ sánh, tùy chọn)</p>\r\n</li>\r\n<li>\r\n<p>Nước cốt dừa (ăn kèm): 200ml</p>\r\n</li>\r\n<li>\r\n<p>Mè rang (vừng): 1–2 muỗng canh</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3>Quy trình chế biến</h3>\r\n<p><strong>Bước 1: Sơ chế đậu xanh</strong></p>\r\n<ul>\r\n<li>\r\n<p>Đậu xanh rửa sạch, nhặt bỏ hạt hư.</p>\r\n</li>\r\n<li>\r\n<p>Ngâm đậu với nước ấm trong 1–2 giờ cho mềm (có thể ngâm qua đêm nếu dùng nước lạnh).</p>\r\n</li>\r\n<li>\r\n<p>Sau khi ngâm, rửa lại đậu một lần nữa cho sạch.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Nấu đậu xanh</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho đậu xanh vào nồi cùng 1 lít nước và lá dứa, đun sôi.</p>\r\n</li>\r\n<li>\r\n<p>Khi nước sôi, hạ nhỏ lửa, hớt bọt thường xuyên để nước chè trong.</p>\r\n</li>\r\n<li>\r\n<p>Nấu đến khi đậu chín mềm, không bị nát (khoảng 20–30 phút).</p>\r\n</li>\r\n<li>\r\n<p>Vớt bỏ lá dứa.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Thêm đường và tạo độ sánh</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho đường và muối vào nồi chè, khuấy đều.</p>\r\n</li>\r\n<li>\r\n<p>Nếu muốn chè sánh nhẹ: hòa tan bột năng hoặc bột sắn với nước lạnh, đổ từ từ vào nồi khi chè đang sôi, khuấy liên tục để không bị vón cục.</p>\r\n</li>\r\n<li>\r\n<p>Đun thêm 3–5 phút cho chè sánh lại rồi tắt bếp.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Nấu nước cốt dừa (ăn kèm, tùy chọn)</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho nước cốt dừa vào nồi nhỏ, thêm chút muối và 1 thìa cà phê đường.</p>\r\n</li>\r\n<li>\r\n<p>Có thể thêm chút bột năng pha loãng để nước cốt sánh mịn.</p>\r\n</li>\r\n<li>\r\n<p>Đun nhỏ lửa, khuấy đều đến khi nước cốt sôi nhẹ và hơi sánh.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 5: Trình bày và thưởng thức</strong></p>\r\n<ul>\r\n<li>\r\n<p>Múc chè ra chén hoặc ly.</p>\r\n</li>\r\n<li>\r\n<p>Rưới nước cốt dừa lên trên.</p>\r\n</li>\r\n<li>\r\n<p>Rắc mè rang nếu thích.</p>\r\n</li>\r\n<li>\r\n<p>Dùng nóng hoặc để nguội, thêm đá nếu ăn lạnh.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3>Lưu ý</h3>\r\n<ul>\r\n<li>\r\n<p>Có thể cho thêm <strong>long nhãn</strong> hoặc <strong>bột báng</strong> để biến tấu món chè phong phú hơn.</p>\r\n</li>\r\n<li>\r\n<p>Không nên nấu chè quá lâu sau khi cho đường vì đậu sẽ bị cứng lại.</p>\r\n</li>\r\n<li>\r\n<p>Nếu không thích độ sánh, có thể bỏ qua bước thêm bột năng.<br></p></li></ul>', 2, 10, 30, 1, '2025-07-16 18:25:21', '2025-07-16 19:09:09'),
	(5, 'Súp Bắp Cua', 'Súp ngọt mềm dễ ăn khai vị', 'https://thechateau.com.vn/wp-content/uploads/2019/11/soup-bap-cua-mon-khai-vi-ngon-tuyet-01-575x335.jpg', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 4–5 người)</strong></p>\r\n<ul>\r\n<li>\r\n<p>Thịt cua đã luộc: 200g (hoặc dùng thịt cua đóng gói/đông lạnh)</p>\r\n</li>\r\n<li>\r\n<p>Trứng gà: 2 quả</p>\r\n</li>\r\n<li>\r\n<p>Bắp mỹ (ngô ngọt): 1 trái lớn hoặc 200g bắp hạt tách sẵn</p>\r\n</li>\r\n<li>\r\n<p>Nấm tuyết hoặc nấm đông cô: 50g (ngâm nở, cắt nhỏ)</p>\r\n</li>\r\n<li>\r\n<p>Lòng trắng trứng: 1–2 cái (tùy khẩu vị, cho màu đẹp)</p>\r\n</li>\r\n<li>\r\n<p>Bột năng (hoặc bột bắp): 2–3 muỗng canh</p>\r\n</li>\r\n<li>\r\n<p>Nước dùng gà (hoặc nước lọc): 1.5–2 lít</p>\r\n</li>\r\n<li>\r\n<p>Hành tím băm, ngò rí, hành lá</p>\r\n</li>\r\n<li>\r\n<p>Gia vị: muối, hạt nêm, tiêu, nước mắm</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<p><strong>Bước 1: Sơ chế nguyên liệu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Nếu dùng cua tươi: luộc cua, gỡ lấy thịt. Nếu dùng cua đóng gói thì rã đông.</p>\r\n</li>\r\n<li>\r\n<p>Bắp mỹ tách hạt, hoặc dùng bắp ngọt đóng hộp thì rửa lại cho sạch nước muối.</p>\r\n</li>\r\n<li>\r\n<p>Nấm ngâm nở, cắt nhỏ vừa ăn.</p>\r\n</li>\r\n<li>\r\n<p>Trứng gà đập ra, đánh tan, tách riêng lòng trắng nếu muốn tạo sợi trắng đẹp.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Nấu nước dùng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Đun sôi khoảng 1.5–2 lít nước dùng gà (hoặc nước lọc có nêm hạt nêm gà).</p>\r\n</li>\r\n<li>\r\n<p>Cho hành tím băm và bắp vào trước, nấu khoảng 5–7 phút cho bắp mềm.</p>\r\n</li>\r\n<li>\r\n<p>Thêm thịt cua, nấm, nêm gia vị gồm muối, hạt nêm, nước mắm theo khẩu vị.</p>\r\n</li>\r\n<li>\r\n<p>Đun thêm 5 phút để các nguyên liệu hoà quyện.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Tạo độ sánh cho súp</strong></p>\r\n<ul>\r\n<li>\r\n<p>Hòa tan bột năng với nước lạnh (không được dùng nước nóng).</p>\r\n</li>\r\n<li>\r\n<p>Khi nước súp đang sôi nhẹ, đổ từ từ hỗn hợp bột năng vào, vừa đổ vừa khuấy đều tay.</p>\r\n</li>\r\n<li>\r\n<p>Đun đến khi súp sánh lại theo ý muốn (có thể điều chỉnh lượng bột năng).</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Tạo vân trứng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Dùng thìa đổ từ từ trứng đã đánh tan (hoặc lòng trắng trứng) vào nồi súp đang sôi nhẹ.</p>\r\n</li>\r\n<li>\r\n<p>Dùng đũa hoặc muỗng khuấy nhẹ theo một chiều để tạo thành vân trứng (giống chỉ trắng).</p>\r\n</li>\r\n<li>\r\n<p>Khi trứng vừa chín là tắt bếp.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 5: Trình bày</strong></p>\r\n<ul>\r\n<li>\r\n<p>Múc súp ra chén, rắc hành lá, ngò rí cắt nhuyễn lên trên.</p>\r\n</li>\r\n<li>\r\n<p>Rắc thêm tiêu xay tùy khẩu vị.</p>\r\n</li>\r\n<li>\r\n<p>Có thể ăn kèm giấm ớt hoặc sa tế cho đậm đà.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Lưu ý</strong></h3>\r\n<ul>\r\n<li>\r\n<p>Có thể thêm <strong>tôm xay hoặc thịt gà xé sợi</strong> để tăng vị ngọt tự nhiên.</p>\r\n</li>\r\n<li>\r\n<p>Trứng không nên khuấy quá mạnh sẽ làm đục súp.</p>\r\n</li>\r\n<li>\r\n<p>Nếu thích ăn súp đặc hơn, tăng thêm bột năng, nhưng không nên quá nhiều sẽ làm bột nặng và mất vị.</p>\r\n</li>\r\n</ul>\r\n<p><br></p>', 2, 15, 40, 1, '2025-07-16 18:25:21', '2025-07-16 19:09:40'),
	(6, 'Gỏi Cuốn', 'Gỏi cuốn nhẹ nhàng, ăn không ngán', 'https://cdn.tgdd.vn/Files/2017/03/22/963738/cach-lam-goi-cuon-tom-thit-thom-ngon-cho-bua-com-gian-don-202203021427281747.jpg', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 4 người – khoảng 16–20 cuốn)</strong></p>\r\n<p><strong>Phần cuốn:</strong></p>\r\n<ul>\r\n<li>\r\n<p>Bánh tráng mỏng: 1 xấp (loại cuốn gỏi)</p>\r\n</li>\r\n<li>\r\n<p>Bún tươi: 300g</p>\r\n</li>\r\n<li>\r\n<p>Tôm sú: 300g (đã bóc vỏ, chừa đuôi nếu muốn đẹp)</p>\r\n</li>\r\n<li>\r\n<p>Thịt ba chỉ luộc: 300g (có thể thay bằng thịt chân giò hoặc thịt nạc vai)</p>\r\n</li>\r\n<li>\r\n<p>Rau sống: xà lách, rau thơm, húng lủi, tía tô, diếp cá…</p>\r\n</li>\r\n<li>\r\n<p>Dưa leo, giá sống (tùy chọn)</p>\r\n</li>\r\n<li>\r\n<p>Hẹ tươi: vài nhánh (để trang trí đuôi cuốn)</p>\r\n</li>\r\n</ul>\r\n<p><strong>Phần nước chấm:</strong><br>\r\n<em>Option 1: Nước mắm chua ngọt</em></p>\r\n<ul>\r\n<li>\r\n<p>Nước mắm ngon: 3 thìa canh</p>\r\n</li>\r\n<li>\r\n<p>Đường: 2 thìa canh</p>\r\n</li>\r\n<li>\r\n<p>Nước cốt chanh: 1–2 thìa</p>\r\n</li>\r\n<li>\r\n<p>Tỏi, ớt băm nhuyễn</p>\r\n</li>\r\n<li>\r\n<p>Nước lọc: 3–4 thìa canh</p>\r\n</li>\r\n</ul>\r\n<p><em>Option 2: Nước chấm tương đậu phộng (kiểu miền Nam)</em></p>\r\n<ul>\r\n<li>\r\n<p>Tương hột xay: 2 thìa canh</p>\r\n</li>\r\n<li>\r\n<p>Nước lọc: 4–5 thìa canh</p>\r\n</li>\r\n<li>\r\n<p>Đường: 1 thìa</p>\r\n</li>\r\n<li>\r\n<p>Bơ đậu phộng: 1 thìa (nếu có)</p>\r\n</li>\r\n<li>\r\n<p>Tỏi phi, ớt băm, đậu phộng rang giã</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<p><strong>Bước 1: Sơ chế nguyên liệu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Tôm rửa sạch, luộc chín, bóc vỏ, bổ đôi theo chiều dọc để khi cuốn dễ nhìn đẹp.</p>\r\n</li>\r\n<li>\r\n<p>Thịt ba chỉ luộc chín tới, để nguội rồi thái lát mỏng.</p>\r\n</li>\r\n<li>\r\n<p>Bún rửa sạch, để ráo.</p>\r\n</li>\r\n<li>\r\n<p>Rau sống rửa kỹ, ngâm nước muối loãng, vớt ra để ráo.</p>\r\n</li>\r\n<li>\r\n<p>Dưa leo (nếu dùng) gọt vỏ, bỏ ruột, cắt sợi dài.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Chuẩn bị nước chấm</strong></p>\r\n<ul>\r\n<li>\r\n<p>Nếu làm nước mắm chua ngọt: hoà tan đường trong nước lọc, sau đó cho nước mắm, nước cốt chanh, tỏi và ớt vào khuấy đều.</p>\r\n</li>\r\n<li>\r\n<p>Nếu làm nước chấm tương: đun tương với nước và đường trên bếp, khuấy đều cho sánh lại. Khi nguội cho thêm bơ đậu phộng và tỏi phi. Rắc đậu phộng giã lên trên khi ăn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Cuốn gỏi</strong></p>\r\n<ul>\r\n<li>\r\n<p>Nhúng bánh tráng vào nước lọc (nhúng nhanh để không bị rách hoặc nhão).</p>\r\n</li>\r\n<li>\r\n<p>Trải bánh tráng lên mặt phẳng.</p>\r\n</li>\r\n<li>\r\n<p>Xếp lần lượt rau sống, bún, thịt, dưa leo (nếu có).</p>\r\n</li>\r\n<li>\r\n<p>Cho 2–3 miếng tôm áp mặt ngoài (để khi cuốn nhìn rõ phần tôm).</p>\r\n</li>\r\n<li>\r\n<p>Thêm cọng hẹ nếu muốn phần đuôi cuốn thò ra đẹp mắt.</p>\r\n</li>\r\n<li>\r\n<p>Cuộn chặt tay từ dưới lên, gấp hai bên mép rồi cuốn tiếp cho đến khi chặt tay và gọn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Trình bày và thưởng thức</strong></p>\r\n<ul>\r\n<li>\r\n<p>Xếp gỏi cuốn ra dĩa.</p>\r\n</li>\r\n<li>\r\n<p>Ăn kèm với nước chấm đã chuẩn bị.</p>\r\n</li>\r\n<li>\r\n<p>Có thể ăn kèm thêm dưa chua, đồ chua hoặc đồ chấm kiểu miền Nam cho đa dạng.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Lưu ý</strong></h3>\r\n<ul>\r\n<li>\r\n<p>Nên chọn bánh tráng dẻo, mỏng, không quá giòn để dễ cuốn và không bị rách.</p>\r\n</li>\r\n<li>\r\n<p>Không nên cuốn quá sớm nếu chưa ăn ngay, bánh tráng sẽ bị khô hoặc bở.</p>\r\n</li>\r\n<li>\r\n<p>Có thể biến tấu bằng cách thay tôm thịt bằng trứng chiên, chả lụa, cá hấp, đậu hũ chiên, v.v. nếu ăn chay hoặc đổi vị.<br></p></li></ul>', 2, 20, 30, 1, '2025-07-16 18:25:21', '2025-07-16 19:10:31'),
	(7, 'Rau Câu Dừa', 'Rau câu ngọt mát từ nước dừa tươi', 'https://cdn.tgdd.vn/Files/2017/08/18/1014229/cach-lam-rau-cau-dua-nhieu-lop-thom-mat-ngon-mieng-202110301706336992.jpg', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 8–10 phần rau câu)</strong></p>\r\n<ul>\r\n<li>\r\n<p>Bột rau câu (agar): 10g (khoảng 1 gói nhỏ)</p>\r\n</li>\r\n<li>\r\n<p>Nước dừa tươi: 1 lít (khoảng 4–5 trái dừa xiêm)</p>\r\n</li>\r\n<li>\r\n<p>Nước cốt dừa: 200ml</p>\r\n</li>\r\n<li>\r\n<p>Đường: 180–200g (gia giảm tùy khẩu vị và độ ngọt của dừa)</p>\r\n</li>\r\n<li>\r\n<p>Lá dứa: 2–3 lá (tùy chọn, tạo mùi thơm)</p>\r\n</li>\r\n<li>\r\n<p>Một ít muối</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<p><strong>Bước 1: Trộn bột rau câu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Trộn đều bột rau câu với đường (giúp rau câu không bị vón khi cho vào nước).</p>\r\n</li>\r\n<li>\r\n<p>Để riêng trong 5–10 phút.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Nấu phần nước dừa</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho 800ml nước dừa tươi vào nồi, thêm lá dứa và đun nóng.</p>\r\n</li>\r\n<li>\r\n<p>Khi nước gần sôi, vừa đổ hỗn hợp bột rau câu + đường vào, vừa khuấy đều tay.</p>\r\n</li>\r\n<li>\r\n<p>Đun sôi lăn tăn khoảng 3–5 phút cho bột rau câu tan hoàn toàn và trong lại.</p>\r\n</li>\r\n<li>\r\n<p>Vớt bỏ lá dứa.</p>\r\n</li>\r\n<li>\r\n<p>Đổ khoảng 2/3 lượng rau câu này vào khuôn (hoặc trái dừa đã nạo bớt cơm), để nguội cho đông lại lớp thứ nhất.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Nấu phần rau câu nước cốt dừa</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho 200ml nước cốt dừa + 200ml nước dừa còn lại + 1 nhúm muối vào nồi.</p>\r\n</li>\r\n<li>\r\n<p>Thêm 1/3 phần rau câu còn lại từ bước 2 vào nấu chung.</p>\r\n</li>\r\n<li>\r\n<p>Khuấy đều trên lửa nhỏ đến khi hỗn hợp sôi nhẹ, mịn, không vón.</p>\r\n</li>\r\n<li>\r\n<p>Khi lớp đầu tiên trong khuôn đã se mặt (nhưng chưa đông cứng hoàn toàn), đổ nhẹ lớp nước cốt dừa này lên trên.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Làm nguội và bảo quản</strong></p>\r\n<ul>\r\n<li>\r\n<p>Để rau câu nguội hoàn toàn ở nhiệt độ phòng.</p>\r\n</li>\r\n<li>\r\n<p>Cho vào ngăn mát tủ lạnh ít nhất 2–3 giờ trước khi ăn để có độ giòn mát.</p>\r\n</li>\r\n<li>\r\n<p>Khi dùng, cắt miếng vừa ăn, có thể rắc thêm chút mè rang nếu thích.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Lưu ý</strong></h3>\r\n<ul>\r\n<li>\r\n<p>Nếu muốn rau câu giòn nhiều thì tăng lượng bột, còn thích mềm mịn thì giảm lượng bột lại một chút.</p>\r\n</li>\r\n<li>\r\n<p>Nên khuấy đều tay khi nấu để rau câu không bị lắng dưới đáy và bị khét.</p>\r\n</li>\r\n<li>\r\n<p>Có thể đổ rau câu trực tiếp vào <strong>trái dừa tươi</strong> để giữ trọn hương vị và tạo hình hấp dẫn hơn.<br></p></li></ul>', 3, 10, 25, 1, '2025-07-16 18:25:21', '2025-07-16 19:11:00'),
	(8, 'Bánh Flan', 'Món bánh mềm mịn tan chảy trong miệng', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8wXb_ANn3IBMcOotnhn9pG9MAtS3FVqb8Hw&s', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 6–8 bánh nhỏ)</strong></p>\r\n<p><strong>Phần bánh:</strong></p>\r\n<ul>\r\n<li>\r\n<p>Trứng gà: 4 quả (hoặc 3 quả trứng + 2 lòng đỏ để flan mịn hơn)</p>\r\n</li>\r\n<li>\r\n<p>Sữa đặc: 1/2 lon (~190g)</p>\r\n</li>\r\n<li>\r\n<p>Sữa tươi không đường: 400ml</p>\r\n</li>\r\n<li>\r\n<p>Vani: 1 ống (tùy chọn, giúp thơm hơn)</p>\r\n</li>\r\n</ul>\r\n<p><strong>Phần caramel:</strong></p>\r\n<ul>\r\n<li>\r\n<p>Đường: 100g</p>\r\n</li>\r\n<li>\r\n<p>Nước lọc: 3 muỗng canh</p>\r\n</li>\r\n<li>\r\n<p>Nước cốt chanh: vài giọt (giúp caramel không bị lại đường)</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<p><strong>Bước 1: Làm caramel</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho đường và nước vào nồi, đun nhỏ lửa.</p>\r\n</li>\r\n<li>\r\n<p>Khi hỗn hợp chuyển sang màu vàng nâu cánh gián (không khuấy), nhỏ vài giọt nước chanh vào.</p>\r\n</li>\r\n<li>\r\n<p>Nhanh tay đổ caramel vào đáy khuôn (hoặc ly, chén sứ) rồi nghiêng nhẹ để caramel phủ đều đáy.</p>\r\n</li>\r\n<li>\r\n<p>Để nguội cho caramel đông lại.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Làm hỗn hợp sữa trứng</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho sữa đặc và sữa tươi vào tô, khuấy đều cho tan (không cần đun nóng).</p>\r\n</li>\r\n<li>\r\n<p>Đánh tan trứng nhẹ nhàng (không đánh mạnh tạo bọt), sau đó lọc qua rây để loại bỏ phần lợn cợn và bọt khí.</p>\r\n</li>\r\n<li>\r\n<p>Trộn trứng vào sữa, khuấy nhẹ tay cho hỗn hợp hoà quyện.</p>\r\n</li>\r\n<li>\r\n<p>Thêm vani, khuấy đều.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Rót hỗn hợp vào khuôn</strong></p>\r\n<ul>\r\n<li>\r\n<p>Đổ từ từ hỗn hợp sữa trứng vào khuôn đã có lớp caramel.</p>\r\n</li>\r\n<li>\r\n<p>Nếu còn bọt trên bề mặt, dùng muỗng vớt bớt hoặc dùng khăn giấy thấm.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Hấp hoặc nướng cách thủy</strong></p>\r\n<p><em>Cách 1: Hấp</em></p>\r\n<ul>\r\n<li>\r\n<p>Đun nước trong xửng cho sôi rồi hạ lửa nhỏ.</p>\r\n</li>\r\n<li>\r\n<p>Đặt khuôn bánh vào nồi hấp, đậy nắp bằng khăn sạch để tránh nước nhỏ xuống mặt bánh.</p>\r\n</li>\r\n<li>\r\n<p>Hấp khoảng 20–30 phút (tùy kích thước khuôn), kiểm tra bánh chín bằng cách dùng tăm xiên thử, nếu rút ra thấy khô là được.</p>\r\n</li>\r\n</ul>\r\n<p><em>Cách 2: Nướng cách thủy</em></p>\r\n<ul>\r\n<li>\r\n<p>Làm nóng lò ở 150–160°C.</p>\r\n</li>\r\n<li>\r\n<p>Đặt khuôn bánh vào khay nước nóng (cao khoảng 1/2 khuôn).</p>\r\n</li>\r\n<li>\r\n<p>Nướng khoảng 35–45 phút. Kiểm tra tương tự như hấp.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 5: Làm nguội và bảo quản</strong></p>\r\n<ul>\r\n<li>\r\n<p>Sau khi bánh chín, để nguội hoàn toàn ở nhiệt độ phòng.</p>\r\n</li>\r\n<li>\r\n<p>Bọc kín rồi cho vào ngăn mát tủ lạnh tối thiểu 2–3 giờ để bánh lạnh và ngon hơn.</p>\r\n</li>\r\n<li>\r\n<p>Khi ăn, úp ngược khuôn ra dĩa để phần caramel chảy đều lên mặt bánh.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Lưu ý</strong></h3>\r\n<ul>\r\n<li>\r\n<p>Không đánh trứng quá mạnh để tránh tạo bọt khí làm rỗ mặt bánh.</p>\r\n</li>\r\n<li>\r\n<p>Lọc hỗn hợp trứng sữa qua rây 1–2 lần giúp bánh mịn mượt hơn.</p>\r\n</li>\r\n<li>\r\n<p>Caramel không nên nấu quá lâu sẽ bị đắng.</p>\r\n</li>\r\n<li>\r\n<p>Nếu muốn bánh béo hơn, có thể thay một phần sữa tươi bằng whipping cream.<br></p></li></ul>', 3, 15, 45, 1, '2025-07-16 18:25:21', '2025-07-16 19:11:31'),
	(9, 'Kem Dừa', 'Kem từ nước cốt dừa và sữa tươi mát lạnh', 'https://www.huongnghiepaau.com/wp-content/uploads/2021/04/kem-con-dao-dua.jpg', '<p><strong style="color: rgb(15, 20, 31); font-family: Urbanist, sans-serif; font-size: 26px;">Nguyên liệu chuẩn bị (cho 4–6 phần)</strong></p>\r\n<ul>\r\n<li>\r\n<p>Nước cốt dừa: 400ml</p>\r\n</li>\r\n<li>\r\n<p>Sữa đặc: 150g (~1/2 lon)</p>\r\n</li>\r\n<li>\r\n<p>Sữa tươi không đường: 200ml</p>\r\n</li>\r\n<li>\r\n<p>Đường: 50–70g (gia giảm theo khẩu vị)</p>\r\n</li>\r\n<li>\r\n<p>Bột năng (hoặc bột bắp): 1–2 muỗng canh</p>\r\n</li>\r\n<li>\r\n<p>Muối: 1/4 thìa cà phê</p>\r\n</li>\r\n<li>\r\n<p>Cơm dừa nạo: 50–100g (tùy thích)</p>\r\n</li>\r\n<li>\r\n<p>Lạc rang, dừa khô, dừa non (ăn kèm, tùy chọn)</p>\r\n</li>\r\n<li>\r\n<p>Vani: 1 ống (hoặc tinh chất dừa nếu muốn đậm mùi hơn)</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Quy trình chế biến</strong></h3>\r\n<p><strong>Bước 1: Trộn nguyên liệu</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho sữa tươi, nước cốt dừa, sữa đặc, đường và muối vào nồi.</p>\r\n</li>\r\n<li>\r\n<p>Khuấy đều hỗn hợp cho hòa tan, sau đó đặt lên bếp đun ở lửa vừa.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 2: Làm sánh hỗn hợp</strong></p>\r\n<ul>\r\n<li>\r\n<p>Hòa tan bột năng với chút nước lạnh.</p>\r\n</li>\r\n<li>\r\n<p>Khi hỗn hợp trên bắt đầu nóng (gần sôi), đổ từ từ bột năng vào, khuấy đều liên tục.</p>\r\n</li>\r\n<li>\r\n<p>Đun đến khi hỗn hợp hơi sánh lại (như sữa đặc lỏng), tắt bếp.</p>\r\n</li>\r\n<li>\r\n<p>Thêm vani hoặc tinh chất dừa vào khuấy đều.</p>\r\n</li>\r\n<li>\r\n<p>Để nguội hoàn toàn ở nhiệt độ phòng.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 3: Làm lạnh và đánh kem</strong></p>\r\n<ul>\r\n<li>\r\n<p>Cho hỗn hợp đã nguội vào hộp, đậy kín và để trong ngăn đá tủ lạnh.</p>\r\n</li>\r\n<li>\r\n<p>Sau 2–3 giờ, lấy ra dùng nĩa hoặc máy đánh trứng đánh tơi để phá vỡ tinh thể đá (giúp kem mịn hơn). Lặp lại bước này 2–3 lần, mỗi lần cách nhau 2–3 tiếng.</p>\r\n</li>\r\n<li>\r\n<p>Sau lần đánh cuối, để kem đông hoàn toàn từ 4–6 tiếng trước khi ăn.</p>\r\n</li>\r\n</ul>\r\n<p><strong>Bước 4: Trình bày và thưởng thức</strong></p>\r\n<ul>\r\n<li>\r\n<p>Múc kem ra ly hoặc trái dừa mini.</p>\r\n</li>\r\n<li>\r\n<p>Rắc thêm lạc rang giã nhỏ, dừa khô, cơm dừa nạo hoặc dừa non thái sợi.</p>\r\n</li>\r\n<li>\r\n<p>Có thể thêm xôi vò, thạch dừa, hoặc siro trái cây tùy thích.</p>\r\n</li>\r\n</ul>\r\n<p><hr></p>\r\n<h3><strong>Lưu ý</strong></h3>\r\n<ul>\r\n<li>\r\n<p>Nếu có máy làm kem, chỉ cần đổ hỗn hợp vào máy sau khi nguội và làm theo hướng dẫn.</p>\r\n</li>\r\n<li>\r\n<p>Có thể thay <strong>bột năng bằng whipping cream</strong> để kem béo ngậy hơn, không cần khuấy bột.</p>\r\n</li>\r\n<li>\r\n<p>Nếu dùng cơm dừa nạo tươi: nên xào sơ với ít đường và muối để tăng hương vị và bảo quản được lâu hơn.<br></p></li></ul>', 3, 15, 60, 1, '2025-07-16 18:25:21', '2025-07-16 19:12:16');

